INSERT INTO `settings` VALUES (1,'Showbiz New','/storage/photos/logonew.png','/storage/photos/logonew.png','contact@showbiznew.vn','0989886059','Địa chỉ: 21 ngõ Tân Thịnh, Đại La, Hai Bà Trưng, Hà Nội','0989886059','https://www.facebook.com/Masti-106807117634294/','1','1','1','1','1','<p>1</p>','New IA','1','1','<p>Quản l&yacute; v&agrave; vận h&agrave;nh bởi:&nbsp;</p>\r\n\r\n<p>SHOWBIZ NEW</p>\r\n\r\n<p>Địa chỉ: 21 ng&otilde; T&acirc;n Thịnh, Đại La, Hai B&agrave; Trưng, H&agrave; Nội</p>\r\n\r\n<p>SĐT:&nbsp;0989886059</p>','2021-04-06 03:33:40','2021-04-29 03:22:56');
